SELECT syntax_terminal_nodes.graph_id, syntax_terminal_nodes.node_id, prosody_nodes.ts, prosody_nodes.word, prosody_nodes.tone, prosody_nodes.accents FROM prosody_nodes JOIN edge ON prosody_nodes.graph_id = edge.source_graph AND prosody_nodes.node_id = edge.source_id JOIN syntax_terminal_nodes ON syntax_terminal_nodes.graph_id = edge.target_graph AND syntax_terminal_nodes.node_id = edge.target_id WHERE edge.grp = 'link_edge'
SELECT graph_id, node_id, terminal_and_prosody.s_num, seq, pos, word, accents, tone, ts, filesequence, sentence_length, sentence_array FROM terminal_and_prosody JOIN sentences ON terminal_and_prosody.s_num = sentences.s_num WHERE accents != '|NONE|' AND tone LIKE '%\\%%' ORDER BY terminal_and_prosody.s_num
SELECT syntax_nonterminal_nodes.node_id, syntax_nonterminal_nodes.graph_id FROM syntax_nonterminal_nodes WHERE syntax_nonterminal_nodes.cat LIKE 'DP[%' EXCEPT SELECT closure.node_id, closure.node_graph FROM syntax_terminal_nodes JOIN closure ON closure.subnode_id = syntax_terminal_nodes.node_id AND closure.node_graph = syntax_terminal_nodes.graph_id WHERE syntax_terminal_nodes.pos LIKE 'A[%'
SELECT prosody_nodes.filesequence, prosody_nodes.graph_id, prosody_nodes.node_id, prosody_nodes.ts, prosody_nodes.word, prosody_nodes.tone, prosody_nodes.accents, syntax_terminal_nodes.graph_id, syntax_terminal_nodes.node_id FROM syntax_terminal_nodes JOIN edge ON syntax_terminal_nodes.graph_id = edge.target_graph AND syntax_terminal_nodes.node_id = edge.target_id JOIN prosody_nodes ON prosody_nodes.graph_id = edge.source_graph AND prosody_nodes.node_id = edge.source_id WHERE edge.grp = 'link_edge'
